import { useEffect } from "react";
import "./MovieList.css";
import axiosPrivate from "../../api/tmdb";

export function MovieList({ movieList, setSelectedId, setMovieList }) {
    const handleClick = (id) => {
        setSelectedId(id);
    }

    useEffect(() => {
        if (movieList.length === 0) {
            const getAllMovies = () => {
                axiosPrivate({
                    url: `https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc&api_key=${process.env.REACT_APP_API_KEY}`,
                })
                    .then((res) => {
                        setMovieList(res.data.results);
                    })
                    .catch((err) => {
                        console.log(err);
                    })
            }
            getAllMovies();
        }
    }, [movieList]);

    return (
        <div className="movie-list">
            {movieList && (
                <div className="movie-list">
                    {movieList.map((movie, id) => (
                        <button
                            key={movie.id}
                            onClick={() => handleClick(movie.id)}>{movie.title}</button>
                    ))}

                </div>
            )}
        </div>
    );
}
